    for key, expected in expected_mappings.items():
        if key in mapping:
            actual = mapping[key]
            if isinstance(expected, list):
                if any(exp.lower() in actual.lower() for exp in expected):
                    correct_mappings += 1
            elif expected.lower() in actual.lower():
                correct_mappings += 1

