    """Evaluate Task 3: Employee Time Tracking and Payroll."""
    results = {
        "points_earned": 0,
        "points_possible": 20,
        "details": {}
    }

    # Evaluate employee hours (8 points)
    hours_points = 0
    hours_details = {}

    for emp_id, key_data in answer_key["task3"]["employee_hours"].items():
