        elif "pure" in product_name:
            pure_products.append(p)

    # Calculate total gallons per grade and total containers
    ev_gallons = 0
    v_gallons = 0
    p_gallons = 0
    total_containers = 0

    for p in extra_virgin_products:
        if "55" in p["unit"]:
            ev_gallons += p["quantity"] * 55
        elif "35" in p["unit"]:
            ev_gallons += p["quantity"] * 35
        elif "5" in p["unit"]:
